import React, { useEffect } from 'react';
import { FiChevronsLeft, FiChevronRight } from 'react-icons/fi';
import { useRouteMatch, Link } from 'react-router-dom';

import logoImg from '../../assests/logo.svg';
import { Header, RepositoryInfo, Issues } from './styles';
import api from '../../services/api';

interface RepositoryParams {
  repository: string;
}

const Repository: React.FC = () => {
  const { params } = useRouteMatch<RepositoryParams>();

  // useEffect(() => {
  //   api.get(`repos/${params.repository}`).then(response => {
  //     console.log(response.data);
  //   });
  // }, [params.repository]);

  // useEffect(() => {
  //   api.get(`repos/${params.repository}/issues`).then(response => {
  //     console.log(response.data);
  //   });
  // }, [params.repository]);

  async function loadData(): Promise<void> {
    const repository = await api.get(`repos/${params.repository}`);
    const issues = await api.get(`repos/${params.repository}/issues`);

    console.log(repository);
    console.log(issues);
  }

  loadData();

  return (
    <>
      <Header>
        <img src={logoImg} alt="Github Explorer" />
        <Link to="/repositories/rocketseat/unform">
          <FiChevronsLeft size={16} />
          Voltar
        </Link>
      </Header>

      <RepositoryInfo>
        <header>
          <img
            src="https://avatars0.githubusercontent.com/u/28929274?v=4"
            alt="RocketSeat"
          />
          <div>
            <strong>rocketseat/unform</strong>
            <p>descrição do repositório</p>
          </div>
        </header>
        <ul>
          <li>
            <strong>1808</strong>
            <span>Stars</span>
          </li>
          <li>
            <strong>48</strong>
            <span>Forks</span>
          </li>
          <li>
            <strong>67</strong>
            <span>Issues abertas</span>
          </li>
        </ul>
      </RepositoryInfo>

      <Issues>
        <Link to="hghg">
          <div>
            <strong>fgf</strong>
            <p>dggggf</p>
          </div>
          <FiChevronRight size={20} />
        </Link>
      </Issues>
    </>
  );
};

export default Repository;
